import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import ServiceManagement from '../components/ServiceManagement';
import BookingManagement from '../components/BookingManagement';

const Dashboard = () => {
  const { user } = useSelector(state => state.auth);
  const [providerData, setProviderData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [createFormData, setCreateFormData] = useState({
    name: '',
    description: '',
    category: 'education',
    subcategory: '',
    address: '',
    service_name: '',
    service_description: '',
    service_type: 'individual',
    duration_hours: 1,
    duration_minutes: 0,
    max_participants: 1,
    pricing_type: 'per_session',
    price: '',
    currency: 'USD',
    pricing_description: '',
    min_sessions: 1,
    max_sessions: null
  });
  const [createLoading, setCreateLoading] = useState(false);

  useEffect(() => {
    if (user?.user_type === 'provider') {
      fetchProviderData();
    } else {
      setLoading(false);
    }
  }, [user]);

  const fetchProviderData = async () => {
    try {
      const response = await axios.get('/api/providers/my/');
      setProviderData(response.data);
    } catch (error) {
      if (error.response?.status === 404) {
        // Provider profile not found - this is expected for new providers
        setProviderData(null);
      } else {
        setError('Failed to load provider data');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleCreateSubmit = async (e) => {
    e.preventDefault();
    setCreateLoading(true);
    setError(null);

    try {
      // Step 1: Create provider profile
      const providerResponse = await axios.post('/api/providers/my/', {
        name: createFormData.name,
        description: createFormData.description,
        category: createFormData.category,
        subcategory: createFormData.subcategory,
        address: createFormData.address
      });

      // Step 2: Create service
      const serviceResponse = await axios.post('/api/providers/services/', {
        name: createFormData.service_name,
        description: createFormData.service_description,
        service_type: createFormData.service_type,
        duration_hours: parseInt(createFormData.duration_hours),
        duration_minutes: parseInt(createFormData.duration_minutes),
        max_participants: parseInt(createFormData.max_participants)
      });

      // Step 3: Create pricing for the service
      await axios.post(`/api/providers/services/${serviceResponse.data.id}/pricing/`, {
        pricing_type: createFormData.pricing_type,
        price: parseFloat(createFormData.price),
        currency: createFormData.currency,
        description: createFormData.pricing_description,
        min_sessions: parseInt(createFormData.min_sessions),
        max_sessions: createFormData.max_sessions ? parseInt(createFormData.max_sessions) : null
      });

      // Refresh provider data
      await fetchProviderData();
      setShowCreateModal(false);
      setCreateFormData({
        name: '',
        description: '',
        category: 'education',
        subcategory: '',
        address: '',
        service_name: '',
        service_description: '',
        service_type: 'individual',
        duration_hours: 1,
        duration_minutes: 0,
        max_participants: 1,
        pricing_type: 'per_session',
        price: '',
        currency: 'USD',
        pricing_description: '',
        min_sessions: 1,
        max_sessions: null
      });
    } catch (error) {
      setError(error.response?.data?.detail || 'Failed to create provider profile and services');
    } finally {
      setCreateLoading(false);
    }
  };

  const handleCreateChange = (e) => {
    setCreateFormData({
      ...createFormData,
      [e.target.name]: e.target.value
    });
  };

  if (loading) {
    return (
      <div className="container py-5">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container py-5">
      {user?.user_type === 'provider' ? (
        <div className="row">
          <div className="col-12">
            <h1>Provider Dashboard</h1>
            <p className="text-muted">Manage your provider profile and services</p>

            {error && (
              <div className="alert alert-danger">{error}</div>
            )}

            {providerData ? (
              <div className="row">
                <div className="col-lg-8">
                  <div className="card mb-4">
                    <div className="card-header">
                      <h5 className="mb-0">Profile Information</h5>
                    </div>
                    <div className="card-body">
                      <div className="row">
                        <div className="col-md-6">
                          <p><strong>Name:</strong> {providerData.name}</p>
                          <p><strong>Category:</strong> {providerData.category}</p>
                          <p><strong>Subcategory:</strong> {providerData.subcategory || 'N/A'}</p>
                        </div>
                        <div className="col-md-6">
                          <p><strong>Status:</strong>
                            <span className={`badge ms-2 ${
                              providerData.is_approved ? 'bg-success' : 'bg-warning'
                            }`}>
                              {providerData.is_approved ? 'Approved' : 'Pending Approval'}
                            </span>
                          </p>
                          <p><strong>Subscription:</strong>
                            <span className={`badge ms-2 ${
                              providerData.subscription_status === 'active' ? 'bg-success' :
                              providerData.subscription_status === 'expired' ? 'bg-danger' : 'bg-secondary'
                            }`}>
                              {providerData.subscription_status}
                            </span>
                          </p>
                          <p><strong>Profile Views:</strong> {providerData.profile_views}</p>
                        </div>
                      </div>
                      <p><strong>Description:</strong> {providerData.description || 'No description provided'}</p>
                      <p><strong>Address:</strong> {providerData.address}</p>
                      {providerData.website && (
                        <p><strong>Website:</strong> <a href={providerData.website} target="_blank" rel="noopener noreferrer">{providerData.website}</a></p>
                      )}
                    </div>
                  </div>
                </div>
                <div className="col-lg-4">
                  <div className="card mb-4">
                    <div className="card-header">
                      <h5 className="mb-0">Quick Actions</h5>
                    </div>
                    <div className="card-body">
                      <div className="d-grid gap-2">
                        <button className="btn btn-primary">Edit Profile</button>
                        <button className="btn btn-outline-primary">Manage Photos</button>
                        <button className="btn btn-outline-primary">View Reviews</button>
                        <button className="btn btn-outline-primary">Subscription</button>
                      </div>
                    </div>
                  </div>

                  <div className="card">
                    <div className="card-header">
                      <h5 className="mb-0">Statistics</h5>
                    </div>
                    <div className="card-body">
                      <div className="row text-center">
                        <div className="col-6">
                          <h3 className="text-primary">{providerData.profile_views}</h3>
                          <small className="text-muted">Views</small>
                        </div>
                        <div className="col-6">
                          <h3 className="text-success">0</h3>
                          <small className="text-muted">Reviews</small>
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* Service Management Section */}
                  <ServiceManagement />
                </div>
              </div>
            ) : (
              <div className="card">
                <div className="card-body text-center">
                  <h5>Complete Your Provider Profile</h5>
                  <p>You haven't set up your provider profile yet.</p>
                  <button
                    className="btn btn-primary"
                    onClick={() => setShowCreateModal(true)}
                  >
                    Create Provider Profile
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      ) : (
        <div className="row">
          <div className="col-12">
            <h1>Student/Parent Dashboard</h1>
            <p className="text-muted">Manage your account and activities</p>

            <div className="row">
              <div className="col-lg-6">
                <div className="card mb-4">
                  <div className="card-header">
                    <h5 className="mb-0">Recent Activity</h5>
                  </div>
                  <div className="card-body">
                    <p className="text-muted">No recent activity</p>
                  </div>
                </div>
              </div>
              <div className="col-lg-6">
                <div className="card mb-4">
                  <div className="card-header">
                    <h5 className="mb-0">Quick Actions</h5>
                  </div>
                  <div className="card-body">
                    <div className="d-grid gap-2">
                      <a href="/search" className="btn btn-primary">Find Providers</a>
                      <button className="btn btn-outline-primary">View History</button>
                      <button className="btn btn-outline-primary">My Reviews</button>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Booking Management Section */}
            <BookingManagement />
          </div>
        </div>
      )}

      {/* Create Provider Profile Modal */}
      {showCreateModal && (
        <div className="modal show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Create Provider Profile</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowCreateModal(false)}
                ></button>
              </div>
              <form onSubmit={handleCreateSubmit}>
                <div className="modal-body">
                  <div className="mb-3">
                    <label htmlFor="name" className="form-label">Provider Name</label>
                    <input
                      type="text"
                      className="form-control"
                      id="name"
                      name="name"
                      value={createFormData.name}
                      onChange={handleCreateChange}
                      placeholder="Enter your provider name"
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="description" className="form-label">Description</label>
                    <textarea
                      className="form-control"
                      id="description"
                      name="description"
                      value={createFormData.description}
                      onChange={handleCreateChange}
                      rows="3"
                      placeholder="Describe your services"
                    ></textarea>
                  </div>
                  <div className="mb-3">
                    <label htmlFor="category" className="form-label">Category</label>
                    <select
                      className="form-control"
                      id="category"
                      name="category"
                      value={createFormData.category}
                      onChange={handleCreateChange}
                    >
                      <option value="education">Education</option>
                      <option value="sports">Sports</option>
                      <option value="arts">Arts</option>
                      <option value="music">Music</option>
                      <option value="technology">Technology</option>
                      <option value="other">Other</option>
                    </select>
                  </div>
                  <div className="mb-3">
                    <label htmlFor="subcategory" className="form-label">Subcategory (Optional)</label>
                    <input
                      type="text"
                      className="form-control"
                      id="subcategory"
                      name="subcategory"
                      value={createFormData.subcategory}
                      onChange={handleCreateChange}
                      placeholder="e.g., Mathematics, Piano, Basketball"
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="address" className="form-label">Address</label>
                    <input
                      type="text"
                      className="form-control"
                      id="address"
                      name="address"
                      value={createFormData.address}
                      onChange={handleCreateChange}
                      placeholder="Enter your location"
                    />
                  </div>
                </div>
                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => setShowCreateModal(false)}
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={createLoading}
                  >
                    {createLoading ? 'Creating...' : 'Create Profile'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
