import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import ServiceManagement from '../components/ServiceManagement';
import BookingManagement from '../components/BookingManagement';

const Dashboard = () => {
  const { user } = useSelector(state => state.auth);
  const [providerData, setProviderData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [createFormData, setCreateFormData] = useState({
    name: '',
    category: '',
    address: '',
    contact_phone: '',
    contact_email: '',
    description: '',
    years_experience: '',
    languages: '',
    qualifications: '',
    target_age_groups: '',
    teaching_methodology: '',
    specializations: '',
    service_name: '',
    service_description: '',
    service_type: 'individual',
    duration_hours: '',
    duration_minutes: '',
    max_participants: '',
    pricing_type: 'per_session',
    price: '',
    currency: 'INR',
    pricing_description: '',
    min_sessions: '',
    max_sessions: '',
  });
  const [createLoading, setCreateLoading] = useState(false);

  useEffect(() => {
    if (user?.user_type === 'provider') {
      fetchProviderData();
    } else {
      setLoading(false);
    }
  }, [user]);

  const fetchProviderData = async () => {
    try {
      const response = await axios.get('/api/providers/my/');
      setProviderData(response.data);
    } catch (error) {
      if (error.response?.status === 404) {
        // Provider profile not found - this is expected for new providers
        setProviderData(null);
      } else {
        setError('Failed to load provider data');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleCreateSubmit = async (e) => {
    e.preventDefault();
    setCreateLoading(true);
    setError(null);

    try {
      // Only send supported fields
      const payload = {
        name: createFormData.name,
        category: createFormData.category,
        address: createFormData.address,
        contact_phone: createFormData.contact_phone,
        contact_email: createFormData.contact_email,
        description: createFormData.description,
      };

      await axios.post('/api/providers/my/', payload);

      await fetchProviderData();
      setShowCreateModal(false);
      setCreateFormData({
        name: '',
        category: '',
        address: '',
        contact_phone: '',
        contact_email: '',
        description: '',
        years_experience: '',
        languages: '',
        qualifications: '',
        target_age_groups: '',
        teaching_methodology: '',
        specializations: '',
        service_name: '',
        service_description: '',
        service_type: 'individual',
        duration_hours: '',
        duration_minutes: '',
        max_participants: '',
        pricing_type: 'per_session',
        price: '',
        currency: 'INR',
        pricing_description: '',
        min_sessions: '',
        max_sessions: '',
      });
    } catch (error) {
      setError(error.response?.data?.detail || 'Failed to create provider profile');
    } finally {
      setCreateLoading(false);
    }
  };

  const handleCreateChange = (e) => {
    const { name, value } = e.target;
    setCreateFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  if (loading) {
    return (
      <div className="container py-5">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container py-5">
      {user?.user_type === 'provider' ? (
        <div className="row">
          <div className="col-12">
            <h1>Provider Dashboard</h1>
            <p className="text-muted">Manage your provider profile and services</p>

            {error && (
              <div className="alert alert-danger">{error}</div>
            )}

            {providerData ? (
              <div className="row">
                <div className="col-lg-8">
                  <div className="card mb-4">
                    <div className="card-header">
                      <h5 className="mb-0">Profile Information</h5>
                    </div>
                    <div className="card-body">
                      <div className="row">
                        <div className="col-md-6">
                          <p><strong>Name:</strong> {providerData.name}</p>
                          <p><strong>Category:</strong> {providerData.category}</p>
                          <p><strong>Subcategory:</strong> {providerData.subcategory || 'N/A'}</p>
                        </div>
                        <div className="col-md-6">
                          <p><strong>Status:</strong>
                            <span className={`badge ms-2 ${
                              providerData.is_approved ? 'bg-success' : 'bg-warning'
                            }`}>
                              {providerData.is_approved ? 'Approved' : 'Pending Approval'}
                            </span>
                          </p>
                          <p><strong>Subscription:</strong>
                            <span className={`badge ms-2 ${
                              providerData.subscription_status === 'active' ? 'bg-success' :
                              providerData.subscription_status === 'expired' ? 'bg-danger' : 'bg-secondary'
                            }`}>
                              {providerData.subscription_status}
                            </span>
                          </p>
                          <p><strong>Profile Views:</strong> {providerData.profile_views}</p>
                        </div>
                      </div>
                      <p><strong>Description:</strong> {providerData.description || 'No description provided'}</p>
                      <p><strong>Address:</strong> {providerData.address}</p>
                      {providerData.website && (
                        <p><strong>Website:</strong> <a href={providerData.website} target="_blank" rel="noopener noreferrer">{providerData.website}</a></p>
                      )}
                    </div>
                  </div>
                </div>
                <div className="col-lg-4">
                  <div className="card mb-4">
                    <div className="card-header">
                      <h5 className="mb-0">Quick Actions</h5>
                    </div>
                    <div className="card-body">
                      <div className="d-grid gap-2">
                        <button className="btn btn-primary">Edit Profile</button>
                        <button className="btn btn-outline-primary">Manage Photos</button>
                        <button className="btn btn-outline-primary">View Reviews</button>
                        <button className="btn btn-outline-primary">Subscription</button>
                      </div>
                    </div>
                  </div>

                  <div className="card">
                    <div className="card-header">
                      <h5 className="mb-0">Statistics</h5>
                    </div>
                    <div className="card-body">
                      <div className="row text-center">
                        <div className="col-6">
                          <h3 className="text-primary">{providerData.profile_views}</h3>
                          <small className="text-muted">Views</small>
                        </div>
                        <div className="col-6">
                          <h3 className="text-success">0</h3>
                          <small className="text-muted">Reviews</small>
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* Service Management Section */}
                  <ServiceManagement />
                </div>
              </div>
            ) : (
              <div className="card">
                <div className="card-body text-center">
                  <h5>Complete Your Provider Profile</h5>
                  <p>You haven't set up your provider profile yet.</p>
                  <button
                    className="btn btn-primary"
                    onClick={() => setShowCreateModal(true)}
                  >
                    Create Provider Profile
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      ) : (
        <div className="row">
          <div className="col-12">
            <h1>Student/Parent Dashboard</h1>
            <p className="text-muted">Manage your account and activities</p>

            <div className="row">
              <div className="col-lg-6">
                <div className="card mb-4">
                  <div className="card-header">
                    <h5 className="mb-0">Recent Activity</h5>
                  </div>
                  <div className="card-body">
                    <p className="text-muted">No recent activity</p>
                  </div>
                </div>
              </div>
              <div className="col-lg-6">
                <div className="card mb-4">
                  <div className="card-header">
                    <h5 className="mb-0">Quick Actions</h5>
                  </div>
                  <div className="card-body">
                    <div className="d-grid gap-2">
                      <a href="/search" className="btn btn-primary">Find Providers</a>
                      <button className="btn btn-outline-primary">View History</button>
                      <button className="btn btn-outline-primary">My Reviews</button>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Booking Management Section */}
            <BookingManagement />
          </div>
        </div>
      )}

      {/* Create Provider Profile Modal */}
      {showCreateModal && (
        <div className="modal show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Create Provider Profile</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowCreateModal(false)}
                ></button>
              </div>
              <form onSubmit={handleCreateSubmit}>
                <div className="modal-body" style={{ maxHeight: '70vh', overflowY: 'auto' }}>
                  {/* Basic Information */}
                  <h6 className="fw-bold mb-3">Basic Information</h6>
                  <div className="mb-3">
                    <label className="form-label">Provider Name</label>
                    <input
                      type="text"
                      className="form-control"
                      name="name"
                      value={createFormData.name}
                      onChange={handleCreateChange}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Category</label>
                    <select
                      className="form-control"
                      name="category"
                      value={createFormData.category}
                      onChange={handleCreateChange}
                      required
                    >
                      <option value="">Select a category</option>
                      <option value="education">Education</option>
                      <option value="sports">Sports</option>
                      <option value="arts">Arts</option>
                      <option value="music">Music</option>
                      <option value="technology">Technology</option>
                      <option value="language">Language</option>
                      <option value="test_prep">Test Preparation</option>
                      <option value="other">Other</option>
                    </select>
                  </div>

                  {/* Location Details */}
                  <h6 className="fw-bold mb-3">Location Details</h6>
                  <div className="mb-3">
                    <label className="form-label">Address</label>
                    <input
                      type="text"
                      className="form-control"
                      name="address"
                      value={createFormData.address}
                      onChange={handleCreateChange}
                      placeholder="Full address for in-person services"
                      required
                    />
                  </div>

                  {/* Contact Details */}
                  <h6 className="fw-bold mb-3">Contact Details</h6>
                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Phone Number</label>
                      <input
                        type="tel"
                        className="form-control"
                        name="contact_phone"
                        value={createFormData.contact_phone}
                        onChange={handleCreateChange}
                      />
                    </div>
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Email Address</label>
                      <input
                        type="email"
                        className="form-control"
                        name="contact_email"
                        value={createFormData.contact_email}
                        onChange={handleCreateChange}
                      />
                    </div>
                  </div>

                  {/* Services Offered */}
                  <h6 className="fw-bold mb-3">Services Offered</h6>
                  <div className="mb-3">
                    <label className="form-label">Detailed Description of Services</label>
                    <textarea
                      className="form-control"
                      name="description"
                      value={createFormData.description}
                      onChange={handleCreateChange}
                      rows="3"
                      required
                    />
                  </div>
                </div>
                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => setShowCreateModal(false)}
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={createLoading}
                  >
                    {createLoading ? 'Creating...' : 'Create Profile'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
