import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchProviders } from '../features/providersSlice';

const Search = () => {
  const dispatch = useDispatch();
  const { providers, loading, error } = useSelector(state => state.providers);

  useEffect(() => {
    dispatch(fetchProviders());
  }, [dispatch]);

  return (
    <div className="search-page container">
      <div className="results-section">
        {/* Results Header */}
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h4>
            {loading ? 'Loading...' : `${providers.length} Providers Found`}
          </h4>
        </div>

        {/* Error Message */}
        {error && (
          <div className="alert alert-danger" role="alert">
            {typeof error === 'string' ? error : JSON.stringify(error)}
          </div>
        )}

        {/* Loading */}
        {loading && (
          <div className="text-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        )}

        {/* Results Grid */}
        {!loading && providers.length > 0 && (
          <div className="row">
            {providers.map((provider) => (
              <div key={provider.id} className="col-lg-6 col-xl-4 mb-4">
                <div className="card h-100">
                  <div className="card-body">
                    <h5 className="card-title">{provider.business_name}</h5>
                    <p className="card-text text-muted small">
                      {provider.services && provider.services.length > 0 ? provider.services[0].category : 'General'} â€¢ {provider.services && provider.services.length > 0 ? provider.services[0].subcategory || 'General' : 'General'}
                    </p>
                    <p className="card-text">
                      <i className="fas fa-map-marker-alt text-muted"></i> {provider.address}
                    </p>

                    {/* Rating */}
                    <div className="mb-2">
                      <span className="badge bg-warning text-dark me-2">
                        <i className="fas fa-star"></i> {provider.average_rating || 'N/A'}
                      </span>
                      <small className="text-muted">
                        ({provider.review_count || 0} reviews)
                      </small>
                    </div>

                    {/* Services */}
                    {provider.services && provider.services.length > 0 && (
                      <div className="mb-2">
                        <small className="text-muted fw-bold">Services:</small>
                        <div className="mt-1">
                          {provider.services.slice(0, 2).map(service => (
                            <span key={service.id} className="badge bg-light text-dark me-1 mb-1">
                              {service.name}
                            </span>
                          ))}
                          {provider.services.length > 2 && (
                            <span className="badge bg-light text-dark">
                              +{provider.services.length - 2} more
                            </span>
                          )}
                        </div>
                      </div>
                    )}
                  </div>

                  <div className="card-footer bg-transparent">
                    <div className="d-flex justify-content-between">
                      <Link to={`/provider/${provider.id}`} className="btn btn-outline-primary btn-sm">
                        View Details
                      </Link>
                      <button className="btn btn-primary btn-sm">
                        Contact
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* No Results */}
        {!loading && providers.length === 0 && !error && (
          <div className="text-center py-5">
            <i className="fas fa-search fa-3x text-muted mb-3"></i>
            <h5>No providers found</h5>
            <p className="text-muted">No providers available at the moment</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Search;
