import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ServiceManagement = () => {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddService, setShowAddService] = useState(false);
  const [editingService, setEditingService] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    service_type: 'individual',
    duration_hours: 1,
    duration_minutes: 0,
    max_participants: 1
  });

  useEffect(() => {
    fetchServices();
  }, []);

  const fetchServices = async () => {
    try {
      const response = await axios.get('/api/providers/services/');
      setServices(response.data);
    } catch (error) {
      console.error('Failed to fetch services:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingService) {
        await axios.put(`/api/providers/services/${editingService.id}/`, formData);
      } else {
        await axios.post('/api/providers/services/', formData);
      }
      fetchServices();
      resetForm();
    } catch (error) {
      console.error('Failed to save service:', error);
    }
  };

  const handleEdit = (service) => {
    setEditingService(service);
    setFormData({
      name: service.name,
      description: service.description || '',
      service_type: service.service_type,
      duration_hours: service.duration_hours,
      duration_minutes: service.duration_minutes,
      max_participants: service.max_participants
    });
    setShowAddService(true);
  };

  const handleDelete = async (serviceId) => {
    if (window.confirm('Are you sure you want to delete this service?')) {
      try {
        await axios.delete(`/api/providers/services/${serviceId}/`);
        fetchServices();
      } catch (error) {
        console.error('Failed to delete service:', error);
      }
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      service_type: 'individual',
      duration_hours: 1,
      duration_minutes: 0,
      max_participants: 1
    });
    setEditingService(null);
    setShowAddService(false);
  };

  if (loading) {
    return (
      <div className="text-center py-3">
        <div className="spinner-border spinner-border-sm" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="mt-4">
      <div className="card">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h5 className="mb-0">My Services</h5>
          <button
            className="btn btn-primary btn-sm"
            onClick={() => setShowAddService(!showAddService)}
          >
            {showAddService ? 'Cancel' : 'Add Service'}
          </button>
        </div>
        <div className="card-body">
          {showAddService && (
            <form onSubmit={handleSubmit} className="mb-4 p-3 border rounded">
              <h6>{editingService ? 'Edit Service' : 'Add New Service'}</h6>
              <div className="row">
                <div className="col-md-6 mb-3">
                  <label className="form-label">Service Name *</label>
                  <input
                    type="text"
                    className="form-control"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label">Service Type</label>
                  <select
                    className="form-select"
                    name="service_type"
                    value={formData.service_type}
                    onChange={handleInputChange}
                  >
                    <option value="individual">Individual Session</option>
                    <option value="group">Group Session</option>
                    <option value="package">Package Deal</option>
                    <option value="consultation">Consultation</option>
                    <option value="assessment">Assessment</option>
                  </select>
                </div>
              </div>
              <div className="mb-3">
                <label className="form-label">Description</label>
                <textarea
                  className="form-control"
                  name="description"
                  value={formData.description}
                  onChange={handleInputChange}
                  rows="3"
                />
              </div>
              <div className="row">
                <div className="col-md-3 mb-3">
                  <label className="form-label">Duration (Hours)</label>
                  <input
                    type="number"
                    className="form-control"
                    name="duration_hours"
                    value={formData.duration_hours}
                    onChange={handleInputChange}
                    min="0"
                    max="24"
                  />
                </div>
                <div className="col-md-3 mb-3">
                  <label className="form-label">Duration (Minutes)</label>
                  <input
                    type="number"
                    className="form-control"
                    name="duration_minutes"
                    value={formData.duration_minutes}
                    onChange={handleInputChange}
                    min="0"
                    max="59"
                  />
                </div>
                <div className="col-md-3 mb-3">
                  <label className="form-label">Max Participants</label>
                  <input
                    type="number"
                    className="form-control"
                    name="max_participants"
                    value={formData.max_participants}
                    onChange={handleInputChange}
                    min="1"
                  />
                </div>
                <div className="col-md-3 mb-3 d-flex align-items-end">
                  <button type="submit" className="btn btn-success me-2">
                    {editingService ? 'Update' : 'Add'} Service
                  </button>
                  <button type="button" className="btn btn-secondary" onClick={resetForm}>
                    Cancel
                  </button>
                </div>
              </div>
            </form>
          )}

          {services.length === 0 ? (
            <p className="text-muted">No services added yet. Click "Add Service" to get started.</p>
          ) : (
            <div className="row">
              {services.map(service => (
                <div key={service.id} className="col-md-6 mb-3">
                  <div className="card h-100">
                    <div className="card-body">
                      <div className="d-flex justify-content-between align-items-start">
                        <div>
                          <h6 className="card-title">{service.name}</h6>
                          <p className="card-text small text-muted mb-2">
                            {service.service_type.replace('_', ' ').toUpperCase()} •
                            Duration: {service.duration_display} •
                            Max: {service.max_participants} participant{service.max_participants > 1 ? 's' : ''}
                          </p>
                          {service.description && (
                            <p className="card-text small">{service.description}</p>
                          )}
                        </div>
                        <div className="dropdown">
                          <button
                            className="btn btn-sm btn-outline-secondary dropdown-toggle"
                            type="button"
                            data-bs-toggle="dropdown"
                          >
                            Actions
                          </button>
                          <ul className="dropdown-menu">
                            <li>
                              <button
                                className="dropdown-item"
                                onClick={() => handleEdit(service)}
                              >
                                Edit
                              </button>
                            </li>
                            <li>
                              <button
                                className="dropdown-item text-danger"
                                onClick={() => handleDelete(service.id)}
                              >
                                Delete
                              </button>
                            </li>
                          </ul>
                        </div>
                      </div>
                      <div className="card-footer bg-transparent">
                        <small className="text-muted">
                          Status: {service.is_active ? 'Active' : 'Inactive'}
                        </small>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ServiceManagement;
